CASE WHEN (LCLAS_ID='70') THEN MONTO ELSE CAST('0' AS DECIMAL(11,2)) END AS MONTO70,
CASE WHEN (LCLAS_ID='71') THEN MONTO ELSE CAST('0' AS DECIMAL(11,2)) END AS MONTO71,
CASE WHEN (LCLAS_ID='72') THEN MONTO ELSE CAST('0' AS DECIMAL(11,2)) END AS MONTO72,
CASE WHEN (LCLAS_ID='73') THEN MONTO ELSE CAST('0' AS DECIMAL(11,2)) END AS MONTO73,
CASE WHEN (LCLAS_ID='74') THEN MONTO ELSE CAST('0' AS DECIMAL(11,2)) END AS MONTO74,
CASE WHEN (LCLAS_ID='75') THEN MONTO ELSE CAST('0' AS DECIMAL(11,2)) END AS MONTO75
FROM(

SELECT FECHA_MIN,FECHA_MAX,A.LRECAU_ID, SUBSTR(A.LRECAU_ID,5,2) AÑO,SUBSTR(A.LRECAU_ID,7,2) MES,SUBSTR(A.LRECAU_ID,9,2) DIA,
B.NESTA_RENAES,B.NEST_NOMBRE,SUM(E.CANTIDAD)CANTIDAD,SUM(E.MONTO)MONTO,F.LCLAS_ID
FROM(
SELECT LRECAU_ID,MIN(LRECAU_FECHA) FECHA_MIN,MAX(LRECAU_FECHA) FECHA_MAX FROM lrecaudacion_deposito GROUP BY LRECAU_ID
) A
INNER JOIN nestablecimiento B
ON SUBSTRING(A.LRECAU_ID,11,5) = B.NESTA_RENAES
INNER JOIN ndistrito C
ON B.NDIST_ID = C.NDIST_ID
INNER JOIN lrecaudacion_tipo D
ON SUBSTRING(A.LRECAU_ID,3,2)=D.LRECTIP_ID
INNER JOIN lrecaudacion_detalle E
ON A.LRECAU_ID=E.LRECAU_ID
INNER JOIN lclasificador F
ON F.LCLAS_ID = E.IDITEM

WHERE A.LRECAU_ID LIKE '01%'
AND (A.FECHA_MIN >= @first_day AND A.FECHA_MAX <= @last_day)
GROUP BY A.FECHA_MIN,A.FECHA_MAX,A.LRECAU_ID,B.NESTA_RENAES,B.NEST_NOMBRE,F.LCLAS_ID
)A

)B
GROUP BY FECHA_MIN,FECHA_MAX,AÑO,MES,DIA,NESTA_RENAES,NEST_NOMBRE
ORDER BY RENAES

)C;




